SELECT OMI.MEMBER_DIVISION, OMI.MEMBER_TEAM, OTI.TEAM_NAME, COUNT(CIL.ISSUE_ID) AS TOTAL_COUNT, SUM(CASE WHEN CIL.ISSUE_CATEGORY = '제품' THEN 1 ELSE 0 END) AS CATEGORY_1, SUM(CASE WHEN CIL.ISSUE_CATEGORY = '서비스' THEN 1 ELSE 0 END) AS CATEGORY_2, SUM(CASE WHEN CIL.ISSUE_CATEGORY = '지원' THEN 1 ELSE 0 END) AS CATEGORY_3, SUM(CASE WHEN CIL.ISSUE_CATEGORY = '기타' THEN 1 ELSE 0 END) AS CATEGORY_4, SUM(CASE WHEN CIL.DUE_DATE >= CURRENT_DATE() AND (CIL.ISSUE_CLOSE_DATE IS NULL OR CIL.ISSUE_CLOSE_DATE = '') THEN 1 ELSE 0 END) AS ISSUE_STATUS_1, SUM(CASE WHEN CURRENT_DATE() > CIL.DUE_DATE AND (CIL.ISSUE_CLOSE_DATE IS NULL OR CIL.ISSUE_CLOSE_DATE = '') THEN 1 ELSE 0 END) AS ISSUE_STATUS_2, SUM(CASE WHEN CIL.ISSUE_CLOSE_DATE IS NOT NULL AND CIL.ISSUE_CLOSE_DATE != '' THEN 1 ELSE 0 END) AS ISSUE_STATUS_3 FROM CLIENT_ISSUE_LOG AS CIL LEFT OUTER JOIN OUR_MEMBERS_INFO AS OMI ON CIL.SALES_REPRESENTIVE_ID = OMI.MEMBER_ID_NUM LEFT OUTER JOIN OUR_MEMBERS_INFO AS OMI2 ON CIL.CREATOR_ID = OMI2.MEMBER_ID_NUM LEFT OUTER JOIN OUR_MEMBERS_INFO AS OMI3 ON CIL.SOLVE_OWNER = OMI3.MEMBER_ID_NUM LEFT OUTER JOIN OUR_MEMBERS_INFO AS OMI4 ON CIL.ISSUE_CONFIRM_ID = OMI4.MEMBER_ID_NUM LEFT OUTER JOIN OUR_TEAM_INFO AS OTI ON OTI.TEAM_NO = OMI.MEMBER_TEAM WHERE 1=1 AND YEAR(CIL.SYS_REGISTER_DATE) = YEAR(#{searchDate}) AND QUARTER(CIL.SYS_REGISTER_DATE) = QUARTER(#{searchDate})
GROUP BY OMI.MEMBER_DIVISION,OMI.MEMBER_TEAM HAVING 1=1
AND OMI.MEMBER_DIVISION = #{searchDivision}
AND OMI.MEMBER_DIVISION = #{searchDivision}
AND OMI.MEMBER_DIVISION = #{searchDivision}
ORDER BY FIELD(OMI.MEMBER_TEAM, #{global_member_team}) DESC, OMI.MEMBER_DIVISION ASC, OMI.MEMBER_DIVISION ASC

SELECT OMI.MEMBER_DIVISION, OMI.MEMBER_TEAM, OMI.MEMBER_ID_NUM, OMI.HAN_NAME,
OMI.POSITION_STATUS,
COUNT(CIL.ISSUE_ID) AS TOTAL_COUNT, SUM(CASE WHEN CIL.ISSUE_CATEGORY = '제품' THEN 1 ELSE 0 END) AS CATEGORY_1, SUM(CASE WHEN CIL.ISSUE_CATEGORY = '서비스' THEN 1 ELSE 0 END) AS CATEGORY_2, SUM(CASE WHEN CIL.ISSUE_CATEGORY = '지원' THEN 1 ELSE 0 END) AS CATEGORY_3, SUM(CASE WHEN CIL.ISSUE_CATEGORY = '기타' THEN 1 ELSE 0 END) AS CATEGORY_4, SUM(CASE WHEN CIL.DUE_DATE >= CURRENT_DATE() AND (CIL.ISSUE_CLOSE_DATE IS NULL OR CIL.ISSUE_CLOSE_DATE = '') THEN 1 ELSE 0 END) AS ISSUE_STATUS_1, SUM(CASE WHEN CURRENT_DATE() > CIL.DUE_DATE AND (CIL.ISSUE_CLOSE_DATE IS NULL OR CIL.ISSUE_CLOSE_DATE = '') THEN 1 ELSE 0 END) AS ISSUE_STATUS_2, SUM(CASE WHEN CIL.ISSUE_CLOSE_DATE IS NOT NULL AND CIL.ISSUE_CLOSE_DATE != '' THEN 1 ELSE 0 END) AS ISSUE_STATUS_3 FROM CLIENT_ISSUE_LOG AS CIL LEFT OUTER JOIN OUR_MEMBERS_INFO AS OMI ON CIL.SALES_REPRESENTIVE_ID = OMI.MEMBER_ID_NUM LEFT OUTER JOIN OUR_MEMBERS_INFO AS OMI2 ON CIL.CREATOR_ID = OMI2.MEMBER_ID_NUM LEFT OUTER JOIN OUR_MEMBERS_INFO AS OMI3 ON CIL.SOLVE_OWNER = OMI3.MEMBER_ID_NUM LEFT OUTER JOIN OUR_MEMBERS_INFO AS OMI4 ON CIL.ISSUE_CONFIRM_ID = OMI4.MEMBER_ID_NUM WHERE 1=1 AND YEAR(CIL.SYS_REGISTER_DATE) = YEAR(#{searchDate}) AND QUARTER(CIL.SYS_REGISTER_DATE) = QUARTER(#{searchDate})
GROUP BY OMI.MEMBER_DIVISION,OMI.MEMBER_TEAM,OMI.MEMBER_ID_NUM HAVING 1=1 AND OMI.MEMBER_DIVISION = #{searchDivision} AND OMI.MEMBER_TEAM = #{searchTeam}
GROUP BY OMI.MEMBER_DIVISION,OMI.MEMBER_TEAM,OMI.MEMBER_ID_NUM HAVING 1=1 AND OMI.MEMBER_DIVISION = #{searchDivision} AND OMI.MEMBER_TEAM = #{searchTeam}
GROUP BY OMI.MEMBER_DIVISION,OMI.MEMBER_TEAM,OMI.MEMBER_ID_NUM HAVING 1=1 AND OMI.MEMBER_DIVISION = #{searchDivision} AND OMI.MEMBER_TEAM = #{searchTeam}
AND OMI.MEMBER_DIVISION = #{searchDivision} AND OMI.MEMBER_TEAM = #{searchTeam} GROUP BY OMI.MEMBER_ID_NUM
AND OMI.MEMBER_DIVISION = #{global_member_division} AND OMI.MEMBER_TEAM = #{global_member_team} GROUP BY OMI.HAN_NAME ORDER BY FIELD(OMI.MEMBER_ID_NUM, #{global_member_id}) DESC, FIELD(OMI.MEMBER_TEAM, #{global_member_team}) DESC

