AND CSL.CSAT_ID IN(${searchPKArray})
AND CSL.CSAT_ID IN('')
AND CONCAT(IFNULL(CSL.CSAT_CATEGORY,''), IFNULL(CSL.CSAT_SUBJECT ,''), IFNULL(OMI.HAN_NAME,'')) REGEXP #{allsearch}
AND CSL.CSAT_SUBJECT LIKE CONCAT('%', #{csat_subject}, '%')
AND CSL.CSAT_SURVEY_ID LIKE CONCAT('%', #{CSAT_SURVEY_ID}, '%')
AND OMI.HAN_NAME LIKE CONCAT('%', #{han_name}, '%')
AND CONCAT(IFNULL(CSL.CSAT_CATEGORY,''), IFNULL(CSL.CSAT_SUBJECT ,'')) REGEXP #{allSearchDetail}
AND CSL.CSAT_CATEGORY = #{detailCategory}
AND DATE_FORMAT(CSL.SYS_UPDATE_DATE, '%Y-%m-%d') = #{SysUpdateDate}
ORDER BY CSL.SYS_UPDATE_DATE ASC ) AS TMP, (SELECT @ROWNUM:=0) AS ROWNUM WHERE 1=1 ORDER BY ROWNUM DESC;

