mapper.select:
SELECT COUNT(*) AS listCount, GROUP_CONCAT(CSL.CSAT_ID) AS CSAT_ID, searchPKArray, FILE_IDS AS FILE_ID, GROUP_CONCAT(CSI.HAN_NAME) AS HAN_NAME, GROUP_CONCAT(CSL.CSAT_SUBJECT) AS CSAT_SUBJECT, GROUP_CONCAT(CSL.CSAT_SURVEY_DATE) AS CSAT_SURVEY_DATE, GROUP_CONCAT(CSL.CSAT_DETAIL) AS CSAT_DETAIL, GROUP_CONCAT(CSDL2.RESP_CUSTOMER_NAME) AS RESP_CUSTOMER_NAME, GROUP_CONCAT(CSDL2.COMPANY_NAME) AS COMPANY_NAME FROM CLIENT_SAT_LOG AS CSL LEFT OUTER JOIN ( SELECT COUNT(CSAT_ID) AS FILE_COUNT, CSAT_ID, FILE_ID, GROUP_CONCAT(FILE_ID) AS FILE_IDS FROM CLIENT_SAT_FILE_STORE GROUP BY CSAT_ID ) AS FC ON FC.CSAT_ID = CSL.CSAT_ID LEFT OUTER JOIN ( SELECT COUNT(CSAT_ID) AS CSAT_DETAIL_COUNT, CSAT_ID, CSAT_DETAIL_ID, GROUP_CONCAT(CSAT_DETAIL_ID) AS searchPKArray FROM CLIENT_SATDETAIL_LOG GROUP BY CSAT_ID ) AS CSDL ON CSL.CSAT_ID = CSDL.CSAT_ID LEFT OUTER JOIN CLIENT_SATDETAIL_LOG AS CSDL2 ON CSL.CSAT_ID = CSDL2.CSAT_ID LEFT OUTER JOIN OUR_MEMBERS_INFO AS CSI ON CSL.CSAT_SURVEY_ID = CSI.MEMBER_ID_NUM WHERE 1=1 AND CSL.CSAT_CATEGORY = #{satisfactionCategory}
AND PM.PROJECT_ID IN(${searchPKArray})
AND PM.PROJECT_ID IN('')
AND CONCAT(IFNULL(CSL.CSAT_SUBJECT,''), IFNULL(CSI.HAN_NAME,''), IFNULL(CSL.CSAT_SURVEY_DATE,''), IFNULL(CSL.CSAT_DETAIL,''), IFNULL(CSDL2.RESP_CUSTOMER_NAME,''), IFNULL(CSDL2.COMPANY_NAME,'')) REGEXP #{searchAll}
AND CCI.COMPANY_NAME = #{searchCompanyName}

SELECT COUNT(*) AS listCount, GROUP_CONCAT(CSL.CSAT_ID) AS CSAT_ID, GROUP_CONCAT(CSDL.CSAT_DETAIL_ID) AS searchPKArray, GROUP_CONCAT(FC.FILE_ID) AS FILE_ID, GROUP_CONCAT(CSI.HAN_NAME) AS HAN_NAME, GROUP_CONCAT(CSL.CSAT_SUBJECT) AS CSAT_SUBJECT, GROUP_CONCAT(CSL.CSAT_SURVEY_DATE) AS CSAT_SURVEY_DATE, GROUP_CONCAT(CSL.CSAT_DETAIL) AS CSAT_DETAIL, GROUP_CONCAT(CSDL.RESP_CUSTOMER_NAME) AS RESP_CUSTOMER_NAME, GROUP_CONCAT(CSDL.COMPANY_NAME) AS COMPANY_NAME FROM CLIENT_SATDETAIL_LOG AS CSDL LEFT OUTER JOIN ( SELECT COUNT(CSAT_ID) AS FILE_COUNT, CSAT_ID, FILE_ID FROM CLIENT_SAT_FILE_STORE GROUP BY CSAT_ID ) AS FC ON FC.CSAT_ID = CSDL.CSAT_ID LEFT OUTER JOIN CLIENT_SAT_LOG AS CSL ON CSL.CSAT_ID = CSDL.CSAT_ID LEFT OUTER JOIN OUR_MEMBERS_INFO AS CSI ON CSL.CSAT_SURVEY_ID = CSI.MEMBER_ID_NUM WHERE 1=1 AND CSL.CSAT_CATEGORY = #{satisfactionCategory}
AND PM.PROJECT_ID IN(${searchPKArray})
AND PM.PROJECT_ID IN('')
AND CONCAT(IFNULL(CSL.CSAT_SUBJECT,''), IFNULL(CSI.HAN_NAME,''), IFNULL(CSL.CSAT_SURVEY_DATE,''), IFNULL(CSL.CSAT_DETAIL,''), IFNULL(CSDL.RESP_CUSTOMER_NAME,''), IFNULL(CSDL.COMPANY_NAME,'')) REGEXP #{searchAll}
AND CCI.COMPANY_NAME = #{searchCompanyName}

SELECT @ROWNUM := @ROWNUM +1 AS ROWNUM, TMP_TABLE.* FROM ( SELECT CSL.CSAT_ID, CSL.CREATOR_ID, CSL.CREATE_DATETIME, CSL.CSAT_SUBJECT, CSL.CSAT_CATEGORY, CSL.CSAT_SURVEY_ID, CSI.MEMBER_POST AS CSAT_SURVEY_POST, CSI.HAN_NAME AS CSAT_SURVEY_NAME, CSL.CSAT_SURVEY_DATE, CSL.CSAT_DETAIL, DATE_FORMAT(CSL.SYS_REGISTER_DATE, '%Y-%m-%d') AS SYS_REGISTER_DATE, DATE_FORMAT(CSL.SYS_UPDATE_DATE, '%Y-%m-%d') AS SYS_UPDATE_DATE, DATE_FORMAT(CSL.SYS_UPDATE_DATE, '%Y-%m-%d %H:%i') AS LATELY_UPDATE_DATE, IFNULL(FC.FILE_COUNT,0) AS FILE_COUNT, IF(CSAT_VALUE_TOTAL != '', ROUND(CSAT_VALUE_TOTAL/TOTAL_COUNT, 1), 0) AS AVG_CSAT_VALUE, IFNULL(TOTAL_COUNT,0) AS TOTAL_COUNT, IFNULL(GREAT_COUNT,0) AS GREAT_COUNT, IFNULL(NICE_COUNT,0) AS NICE_COUNT, IFNULL(GOOD_COUNT,0) AS GOOD_COUNT, IFNULL(BAD_COUNT,0) AS BAD_COUNT, IFNULL(HATE_COUNT,0) AS HATE_COUNT, OMI.HAN_NAME, IFNULL(ISSUE_COUNT,0) AS ISSUE_COUNT, IFNULL(ISSUE_STATUS_1,0) AS ISSUE_STATUS_1, IFNULL(ISSUE_STATUS_2,0) AS ISSUE_STATUS_2, IFNULL(ISSUE_STATUS_3,0) AS ISSUE_STATUS_3, CSDL.COMPANY_NAME, CSDL.RESP_CUSTOMER_NAME FROM CLIENT_SAT_LOG AS CSL LEFT OUTER JOIN ( SELECT COUNT(CSAT_ID) AS FILE_COUNT, CSAT_ID FROM CLIENT_SAT_FILE_STORE GROUP BY CSAT_ID ) AS FC ON FC.CSAT_ID = CSL.CSAT_ID LEFT OUTER JOIN OUR_MEMBERS_INFO AS OMI ON CSL.CREATOR_ID = OMI.MEMBER_ID_NUM LEFT OUTER JOIN OUR_MEMBERS_INFO AS CSI ON CSL.CSAT_SURVEY_ID = CSI.MEMBER_ID_NUM LEFT OUTER JOIN ( SELECT CSAT_ID, CSAT_VALUE, COMPANY_NAME, RESP_CUSTOMER_NAME, SUM(IF(CSAT_VALUE='1', 1, 0)) AS HATE_COUNT, SUM(IF(CSAT_VALUE='2', 1, 0)) AS BAD_COUNT, SUM(IF(CSAT_VALUE='3', 1, 0)) AS GOOD_COUNT, SUM(IF(CSAT_VALUE='4', 1, 0)) AS NICE_COUNT, SUM(IF(CSAT_VALUE='5', 1, 0)) AS GREAT_COUNT, COUNT(CSAT_VALUE) AS TOTAL_COUNT, SUM(CSAT_VALUE) AS CSAT_VALUE_TOTAL FROM CLIENT_SATDETAIL_LOG GROUP BY CSAT_ID ) AS CSDL ON CSL.CSAT_ID = CSDL.CSAT_ID LEFT OUTER JOIN ( SELECT CSLD2.CSAT_ID, COUNT(CIL.ISSUE_ID) AS ISSUE_COUNT, SUM(IF(CIL.ISSUE_STATUS='1', 1, 0)) AS ISSUE_STATUS_1, SUM(IF(CIL.ISSUE_STATUS='2', 1, 0)) AS ISSUE_STATUS_2, SUM(IF(CIL.ISSUE_STATUS='3', 1, 0)) AS ISSUE_STATUS_3 FROM CLIENT_SATDETAIL_LOG AS CSLD2 INNER JOIN CLIENT_ISSUE_LOG AS CIL ON CSLD2.CSAT_DETAIL_ID = CIL.CSAT_DETAIL_ID GROUP BY CSLD2.CSAT_ID ) AS DIS ON DIS.CSAT_ID = CSL.CSAT_ID
WHERE 1=1 AND CSL.CSAT_CATEGORY = #{satisfactionCategory}
AND CSDL.CSAT_ID IN(${searchPKArray})
AND CSDL.CSAT_ID IN('')
AND CONCAT(IFNULL(CSL.CSAT_SUBJECT,''), IFNULL(CSI.HAN_NAME,''), IFNULL(CSL.CSAT_SURVEY_DATE,''), IFNULL(CSL.CSAT_DETAIL,''), IFNULL(CSDL.RESP_CUSTOMER_NAME,''), IFNULL(CSDL.COMPANY_NAME,'')) REGEXP #{searchAll}
) AS TMP_TABLE, (SELECT @ROWNUM:=0) AS ROWNUM WHERE 1=1
ORDER BY ${sortCategory}
ORDER BY TMP_TABLE.LATELY_UPDATE_DATE DESC

SELECT @ROWNUM := @ROWNUM +1 AS ROWNUM, TMP_TABLE.* FROM ( SELECT CSDL.CSAT_DETAIL_ID, CSDL.CSAT_ID, CSDL.COMPANY_ID, CSDL.COMPANY_NAME, CSDL.RESP_CUSTOMER_NAME, CSDL.CSAT_SURVEY_DATE, CSDL.MEMBER_ID_NUM, CSDL.CSAT_REG_DATE, CSDL.CSAT_METHOD, CSDL.CSAT_VALUE, CSDL.CSAT_DETAIL AS CSDL_DETAIL, DATE_FORMAT(CSDL.SYS_REGISTER_DATE, '%Y-%m-%d') AS SYS_REGISTER_DATE, DATE_FORMAT(CSDL.SYS_UPDATE_DATE, '%Y-%m-%d') AS SYS_UPDATE_DATE, IFNULL(DATE_FORMAT(CSDL.SYS_UPDATE_DATE, '%Y-%m-%d %H:%i'),NOW()) AS LATELY_UPDATE_DATE, IFNULL(FC.FILE_COUNT,0) AS FILE_COUNT, CSL.CREATOR_ID, CSL.CREATE_DATETIME, CSL.CSAT_SUBJECT, CSL.CSAT_CATEGORY, CSL.CSAT_DETAIL AS CSL_DETAIL, CMI.HAN_NAME AS CSAT_SURVEY_NAME, CMI.MEMBER_POST AS CSAT_SURVEY_POST FROM CLIENT_SATDETAIL_LOG AS CSDL LEFT OUTER JOIN ( SELECT COUNT(CSAT_ID) AS FILE_COUNT, CSAT_ID FROM CLIENT_SAT_FILE_STORE GROUP BY CSAT_ID ) AS FC ON FC.CSAT_ID = CSDL.CSAT_ID LEFT OUTER JOIN CLIENT_SAT_LOG AS CSL ON CSL.CSAT_ID = CSDL.CSAT_ID LEFT OUTER JOIN OUR_MEMBERS_INFO AS CMI ON CMI.MEMBER_ID_NUM = CSDL.MEMBER_ID_NUM LEFT OUTER JOIN OUR_MEMBERS_INFO AS CSI ON CSL.CSAT_SURVEY_ID = CSI.MEMBER_ID_NUM WHERE 1=1 AND CSL.CSAT_CATEGORY = #{satisfactionCategory}
AND CSDL.CSAT_ID IN(${searchPKArray})
AND CSDL.CSAT_ID IN('')
AND CONCAT(IFNULL(CSL.CSAT_SUBJECT,''), IFNULL(CSI.HAN_NAME,''), IFNULL(CSL.CSAT_SURVEY_DATE,''), IFNULL(CSL.CSAT_DETAIL,''), IFNULL(CSDL.RESP_CUSTOMER_NAME,''), IFNULL(CSDL.COMPANY_NAME,'')) REGEXP #{searchAll}
) AS TMP_TABLE, (SELECT @ROWNUM:=0) AS ROWNUM WHERE 1=1
ORDER BY ${sortCategory}
ORDER BY TMP_TABLE.LATELY_UPDATE_DATE DESC

